  `Similarity` int(5) DEFAULT '0' COMMENT '试题在题库中的相似度，相似度越高，质量越低',
  `isunique` tinyint(2) DEFAULT NULL,
  `md52` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `index_tid_fromsite` (`fromSite`,`tiid`) USING BTREE,
  UNIQUE KEY `index_md5_2` (`md52`) USING BTREE,
  KEY `index_subject_fromsite` (`subjectId`,`fromSite`,`isKonw`) USING BTREE,
